public class adminController extends Controller {    Admin admin = new Admin();    public void index() {        renderJson(admin.getAdmin());    }    public void insert() {        String name = getPara("name");        String password = getPara("password");        renderJson(admin.insertAdmin(name,password));    }    public void update() {        int id = getParaToInt("id");        String name = getPara("name");        String password = getPara("password");        renderJson(admin.updateAdmin(id,name,password));    }    public void delete(){        int id=getParaToInt("id");        renderJson(admin.deleteAdmin(id));    }}