public class hotel_bookController extends Controller {    Hotel_book hotel_book = new Hotel_book();    public void index() {        renderJson(hotel_book.getHotel_book());    }    public void insert() {        int hotel = getParaToInt("hotel");        int visitor = getParaToInt("visitor");        String time = getPara("time");        renderJson(hotel_book.insertHotel_book(hotel,visitor,time));    }    public void update() {        int id = getParaToInt("id");        int hotel = getParaToInt("hotel");        int visitor = getParaToInt("visitor");        String time = getPara("time");        renderJson(hotel_book.updateHotel_book(id,hotel,visitor,time));    }    public void delete(){        int id=getParaToInt("id");        renderJson(hotel_book.deleteHotel_book(id));    }}