public class Hotel_book extends BaseHotel_book<Hotel_book> {    public List getHotel_book(){        return Hotel_book.dao.find("select * from hotel_book");    }    public boolean insertHotel_book(int hotel,int visitor,String time) {        return new Hotel_book().set("hotel",hotel).set("visitor",visitor).set("time",time).save();    }    public boolean updateHotel_book(int id,int hotel,int visitor,String time) {        return Hotel_book.dao.findById(id).setHotel(hotel).setVisitor(visitor).setTime(time).update();    }    public boolean deleteHotel_book(int id){        return Hotel_book.dao.findById(id).delete();    }}