public class Travel_news extends BaseTravel_news<Travel_news> {    public List getTravel_news(){        return Travel_news.dao.find("select * from travel_news");    }    public boolean insertTravel_news(String title,String message,String pic,String time) {        return new Travel_news().set("title",title).set("message",message).set("pic",pic).set("time",time).save();    }    public boolean updateTravel_news(int id,String title,String message,String pic,String time) {        return Travel_news.dao.findById(id).setTitle(title).setMessage(message).setPic(pic).setTime(time).update();    }    public boolean deleteTravel_news(int id){        return Travel_news.dao.findById(id).delete();    }}