public class View_hotel extends BaseView_hotel<View_hotel> {    public List getView_hotel(){        return View_hotel.dao.find("select * from view_hotel");    }    public boolean insertView_hotel(int hotel_id,int view_id) {        return new View_hotel().set("hotel_id",hotel_id).set("view_id",view_id).save();    }    public boolean updateView_hotel(int id,int hotel_id,int view_id) {        return View_hotel.dao.findById(id).setHotel_id(hotel_id).setView_id(view_id).update();    }    public boolean deleteView_hotel(int id){        return View_hotel.dao.findById(id).delete();    }}