public class Visitor extends BaseVisitor<Visitor> {    public List getVisitor(){        return Visitor.dao.find("select * from visitor");    }    public boolean insertVisitor(String user_name,String password,String phone) {        return new Visitor().set("user_name",user_name).set("password",password).set("phone",phone).save();    }    public boolean updateVisitor(int id,String user_name,String password,String phone) {        return Visitor.dao.findById(id).setUser_name(user_name).setPassword(password).setPhone(phone).update();    }    public boolean deleteVisitor(int id){        return Visitor.dao.findById(id).delete();    }}