public class travel_newsController extends Controller {    Travel_news travel_news = new Travel_news();    public void index() {        renderJson(travel_news.getTravel_news());    }    public void insert() {        String title = getPara("title");        String message = getPara("message");        String pic = getPara("pic");        String time = getPara("time");        renderJson(travel_news.insertTravel_news(title,message,pic,time));    }    public void update() {        int id = getParaToInt("id");        String title = getPara("title");        String message = getPara("message");        String pic = getPara("pic");        String time = getPara("time");        renderJson(travel_news.updateTravel_news(id,title,message,pic,time));    }    public void delete(){        int id=getParaToInt("id");        renderJson(travel_news.deleteTravel_news(id));    }}