public class view_hotelController extends Controller {    View_hotel view_hotel = new View_hotel();    public void index() {        renderJson(view_hotel.getView_hotel());    }    public void insert() {        int hotel_id = getParaToInt("hotel_id");        int view_id = getParaToInt("view_id");        renderJson(view_hotel.insertView_hotel(hotel_id,view_id));    }    public void update() {        int id = getParaToInt("id");        int hotel_id = getParaToInt("hotel_id");        int view_id = getParaToInt("view_id");        renderJson(view_hotel.updateView_hotel(id,hotel_id,view_id));    }    public void delete(){        int id=getParaToInt("id");        renderJson(view_hotel.deleteView_hotel(id));    }}