public class visitorController extends Controller {    Visitor visitor = new Visitor();    public void index() {        renderJson(visitor.getVisitor());    }    public void insert() {        String user_name = getPara("user_name");        String password = getPara("password");        String phone = getPara("phone");        renderJson(visitor.insertVisitor(user_name,password,phone));    }    public void update() {        int id = getParaToInt("id");        String user_name = getPara("user_name");        String password = getPara("password");        String phone = getPara("phone");        renderJson(visitor.updateVisitor(id,user_name,password,phone));    }    public void delete(){        int id=getParaToInt("id");        renderJson(visitor.deleteVisitor(id));    }}