<?phpinclude_once("conn.php");if(isset($_SESSION['type']))    $type=$_SESSION['type'];else    $type=0;$stmt = $conn->prepare("select title,image,url,web.`name` as `from`,price from shopping,web where style = ? and web.id=`from`");$stmt->bind_param('s', $type);$stmt->execute();$result = $stmt->get_result();$rowArray = $result->fetch_all(MYSQLI_ASSOC);$resultarr = array();foreach ($rowArray as $arr) {    $time = count($arr);    $keyarr = array_keys($arr);    $i = 0;    array_push($resultarr, $arr);}$return["data"] = $resultarr;echo json_encode($return, JSON_UNESCAPED_UNICODE);$stmt->close();mysqli_close($conn);