<?phpinclude_once("conn.php");if(isset($_SESSION['type']))    $type=$_SESSION['type'];else    $type=0;$start=$_POST["start"];$limit=$_POST["limit"];$stmt = $conn->prepare("select title,`describe`,image,url,web.`name` as `from`,price,create_time from shopping,web where style = ? and web.id=`from` limit ?,?");$stmt->bind_param('sii', $type,$start,$limit);$stmt->execute();$result = $stmt->get_result();$rowArray = $result->fetch_all(MYSQLI_ASSOC);$resultarr = array();foreach ($rowArray as $arr) {    $time = count($arr);    $keyarr = array_keys($arr);    $i = 0;    array_push($resultarr, $arr);}$return["data"] = $resultarr;echo json_encode($return, JSON_UNESCAPED_UNICODE);$stmt->close();mysqli_close($conn);