public class adminController extends Controller {    Admin admin = new Admin();    public void index() {        renderJson(admin.getAdmin());    }    public void insert() {        String admin_name = getPara("admin_name");        String password = getPara("password");        renderJson(admin.insertAdmin(admin_name,password));    }    public void update() {        int id = getParaToInt("id");        String admin_name = getPara("admin_name");        String password = getPara("password");        renderJson(admin.updateAdmin(id,admin_name,password));    }    public void delete(){        int id=getParaToInt("id");        renderJson(admin.deleteAdmin(id));    }}