public class departmentController extends Controller {    Department department = new Department();    public void index() {        renderJson(department.getDepartment());    }    public void insert() {        String department = getPara("department");        renderJson(department.insertDepartment(department));    }    public void update() {        int id = getParaToInt("id");        String department = getPara("department");        renderJson(department.updateDepartment(id,department));    }    public void delete(){        int id=getParaToInt("id");        renderJson(department.deleteDepartment(id));    }}