public class Admin extends BaseAdmin<Admin> {    public List getAdmin(){        return Admin.dao.find("select * from admin");    }    public boolean insertAdmin(String admin_name,String password) {        return new Admin().set("admin_name",admin_name).set("password",password).save();    }    public boolean updateAdmin(int id,String admin_name,String password) {        return Admin.dao.findById(id).setAdmin_name(admin_name).setPassword(password).update();    }    public boolean deleteAdmin(int id){        return Admin.dao.findById(id).delete();    }}