public class Department extends BaseDepartment<Department> {    public List getDepartment(){        return Department.dao.find("select * from department");    }    public boolean insertDepartment(String department) {        return new Department().set("department",department).save();    }    public boolean updateDepartment(int id,String department) {        return Department.dao.findById(id).setDepartment(department).update();    }    public boolean deleteDepartment(int id){        return Department.dao.findById(id).delete();    }}