public class Rate extends BaseRate<Rate> {    public List getRate(){        return Rate.dao.find("select * from rate");    }    public boolean insertRate(int rater,int score) {        return new Rate().set("rater",rater).set("score",score).save();    }    public boolean updateRate(int id,int rater,int score) {        return Rate.dao.findById(id).setRater(rater).setscore(score).update();    }    public boolean deleteRate(int id){        return Rate.dao.findById(id).delete();    }}