public class Rules extends BaseRules<Rules> {    public List getRules(){        return Rules.dao.find("select * from rules");    }    public boolean insertRules(String rule) {        return new Rules().set("rule",rule).save();    }    public boolean updateRules(int id,String rule) {        return Rules.dao.findById(id).setRule(rule).update();    }    public boolean deleteRules(int id){        return Rules.dao.findById(id).delete();    }}