public class rateController extends Controller {    Rate rate = new Rate();    public void index() {        renderJson(rate.getRate());    }    public void insert() {        int rater = getParaToInt("rater");        int score = getParaToInt("score");        renderJson(rate.insertRate(rater,score));    }    public void update() {        int id = getParaToInt("id");        int rater = getParaToInt("rater");        int score = getParaToInt("score");        renderJson(rate.updateRate(id,rater,score));    }    public void delete(){        int id=getParaToInt("id");        renderJson(rate.deleteRate(id));    }}