public class rulesController extends Controller {    Rules rules = new Rules();    public void index() {        renderJson(rules.getRules());    }    public void insert() {        String rule = getPara("rule");        renderJson(rules.insertRules(rule));    }    public void update() {        int id = getParaToInt("id");        String rule = getPara("rule");        renderJson(rules.updateRules(id,rule));    }    public void delete(){        int id=getParaToInt("id");        renderJson(rules.deleteRules(id));    }}