public class studentController extends Controller {    Student student = new Student();    public void index() {        renderJson(student.getStudent());    }    public void insert() {        String user_name = getPara("user_name");        String real_name = getPara("real_name");        String password = getPara("password");        String phone = getPara("phone");        int department = getParaToInt("department");        renderJson(student.insertStudent(user_name,real_name,password,phone,department));    }    public void update() {        int id = getParaToInt("id");        String user_name = getPara("user_name");        String real_name = getPara("real_name");        String password = getPara("password");        String phone = getPara("phone");        int department = getParaToInt("department");        renderJson(student.updateStudent(id,user_name,real_name,password,phone,department));    }    public void delete(){        int id=getParaToInt("id");        renderJson(student.deleteStudent(id));    }}